package jay;

public class Datatypes {
	static String a;
	static int b;
	static float c;
	static long d;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		a ="Jay";
		b=20;
		c=2000000000;
		d=20000038;
		
	System.out.println("String : " +a);
	System.out.println("Integer : " +b);
	System.out.println("Float : " +c);
	System.out.println("Long : " +d);
	}

}
